import React from 'react';
import { ReactComponent as AppStoreBadge } from '../assets/app-store-badge.svg'; // Ensure you place the SVG in the appropriate directory

const HeroSection = () => {
  return (
    <div className="bg-black text-white flex flex-col justify-center h-full p-8 md:p-16">
      <h1 className="text-4xl font-bold mb-4">Streamline Your Job Application Process with Drop2Fill Extension</h1>
      <p className="text-lg mb-8">Simplify and Accelerate Your Job Applications</p>
      <a href="https://www.apple.com/app-store/" target="_blank" rel="noopener noreferrer">
        <AppStoreBadge className="w-40 h-auto" />
      </a>
    </div>
  );
};

export default HeroSection;
