import React from 'react';
import { ReactComponent as AppStoreBadge } from '../assets/app-store-badge.svg'; // Ensure you place the SVG in the appropriate directory

const HeroSection = () => {
  return (
    <section className="bg-blue-50 py-16">
      <div className="container mx-auto text-center">
        <h1 className="text-3xl font-bold mb-4">Streamline Your Job Application Process with Drop2Fill Extension</h1>
        <p className="text-xl mb-8">Simplify and Accelerate Your Job Applications</p>
        <div className="flex justify-center space-x-4 mt-4">
          <a href="https://www.apple.com/app-store/" target="_blank" rel="noopener noreferrer">
            <AppStoreBadge className="w-40 h-auto" />
          </a>
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
