import React from 'react';
import { ReactComponent as UserIcon } from '../assets/user.svg';
import { ReactComponent as LockIcon } from '../assets/lock.svg';
import { ReactComponent as ClockIcon } from '../assets/clock.svg';

const WhyDrop2Fill = () => {
  return (
    <section className="py-16 bg-black text-white">
      <h2 className="text-center text-3xl font-bold mb-8">Why Drop2Fill?</h2>
      <div className="flex flex-col md:flex-row justify-center items-center space-y-8 md:space-y-0 md:space-x-8">
        <div className="text-center w-80">
          <UserIcon className="mx-auto mb-4 w-12 h-12" />
          <h3 className="text-xl font-semibold">User-friendly Interface</h3>
          <p>Designed with simplicity in mind, Drop2Fill is intuitive and easy to use, whether you prefer dark mode or light mode.</p>
        </div>
        <div className="text-center w-80">
          <LockIcon className="mx-auto mb-4 w-12 h-12" />
          <h3 className="text-xl font-semibold">Secure and Private</h3>
          <p>Your data is stored locally on your device. We prioritize your privacy and ensure that your information is secure.</p>
        </div>
        <div className="text-center w-80">
          <ClockIcon className="mx-auto mb-4 w-12 h-12" />
          <h3 className="text-xl font-semibold">Time-saving</h3>
          <p>Say goodbye to hours of repetitive work. Drop2Fill helps you in the application process, so you can focus on what matters most.</p>
        </div>
      </div>
    </section>
  );
};

export default WhyDrop2Fill;
