// src/components/Features.jsx
import React from 'react';

const Features = () => {
  return (
    <section className="py-20">
      <div className="container mx-auto text-center">
        <h2 className="text-4xl font-bold mb-12">Why Drop2Fill?</h2>
        <div className="flex flex-wrap justify-center space-x-8">
          <div className="w-1/3 text-left">
            <h3 className="text-2xl font-bold mb-4">User-friendly Interface</h3>
            <p>Designed with simplicity in mind, Drop2Fill is intuitive and easy to use, whether you prefer dark mode or light mode.</p>
          </div>
          <div className="w-1/3 text-left">
            <h3 className="text-2xl font-bold mb-4">Secure and Private</h3>
            <p>Your data is stored locally on your device. We prioritize your privacy and ensure that your information is secure.</p>
          </div>
          <div className="w-1/3 text-left">
            <h3 className="text-2xl font-bold mb-4">Time-saving</h3>
            <p>Say goodbye to hours of repetitive work. Drop2Fill helps you in the application process, so you can focus on what matters most.</p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Features;
