import React from 'react';
import { ReactComponent as AppStoreIcon } from '../assets/app-store.svg';

const HeroSection = () => {
  return (
    <section className="py-16 bg-black text-white flex flex-col justify-center h-full p-8 md:p-16 md:w-1/3">
      <div>
        <h1 className="text-4xl font-bold mb-4">
          Streamline Your Job Application Process with Drop2Fill Extension
        </h1>
        <p className="text-lg mb-8">Simplify and Accelerate Your Job Applications</p>
        <div className="flex justify-center">
          <AppStoreIcon className="h-12 w-48" />
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
